#include <string>

std::string removeVowels(std::string str) {
    std::string result;
    for(int i = 0; i < str.length(); i++) {
        char c = str[i];
        if(c != 'a' && c != 'e' && c != 'i' && c != 'o' && c != 'u' &&
           c != 'A' && c != 'E' && c != 'I' && c != 'O' && c != 'U') {
            result += c;
        }
    }
    return result;
}
